INSERT INTO public.gamification_challenges (id, title, description, detailed_description, image_url, points, start_date, end_date, type, is_active, target_user_categories, evaluation_type, evaluation_config, created_by, created_at, updated_at, display_order) VALUES (6, 'Explore a IA para suas aulas - Notebooklm (Parte 1)', 'Atividade da gamificação sobre a ferramenta de inteligência artificial do Google, chamada NotebookLM', '<p><strong>## 🎯 Objetivo</strong></p><p>Explorar a frramenta de inteligência artificial&nbsp;<a href="https://notebooklm.google.com/" rel="noopener noreferrer" target="_blank">NotebookLM</a>&nbsp;do Google, integrá-lo ao Gemini e produzir materiais de apoio.</p><p><br></p><p><strong>## 📚 Conteúdos de apoio</strong></p><p>1.&nbsp;[<a href="https://www.youtube.com/watch?v=FQvKeCK0Ha8" rel="noopener noreferrer" target="_blank">Vídeo IA Revolution - Tutorial NotebookLM</a>&nbsp;]</p><p>2.&nbsp;[<a href="https://www.youtube.com/watch?v=jrirKVnZ0dQ" rel="noopener noreferrer" target="_blank">Vídeo InvestNews – NotebookLM + Gemini</a>]</p><p>3. [<a href="https://macmagazine.com.br/post/2025/06/03/notebooklm-do-google-agora-suporta-compartilhamento-publico-por-link/" rel="noopener noreferrer" target="_blank">Artigo MacMagazine – Compartilhamento público</a>]</p><p><br></p><p><strong>## 📝 Como ganhar os pontos</strong></p><p><strong>- </strong>Responda ao quiz abaixo para garantir seus pontos (acertar pelo menos 4/5).</p><p><br></p><p><strong>Obs: Volte à página principal da gamificação e acesse a parte 2 deste desafio para garantir mais 60 pontos!</strong></p>', 'https://diplo-media.s3.eu-central-1.amazonaws.com/2025/07/Google-Gemini-NotebookLM-AI-1024x576.png', 50, '2025-08-14 00:00:00', '2025-09-13 00:00:00', 'periodic', true, '{}', 'quiz', '{"quiz": {"minScore": 80, "questions": [{"id": "1755023496017", "options": ["É um editor de vídeos profissional para criar conteúdo para o YouTube.", "É uma ferramenta para criar apresentações de slides de forma automática.", "É um assistente de pesquisa e estudos que usa IA para centralizar, resumir e criar novos formatos a partir de suas fontes de informação.", "É um substituto do Google Drive para armazenamento de arquivos na nuvem."], "question": "Qual é a principal função do Google NotebookLM, conforme apresentado nos vídeos?", "correctAnswer": 2}, {"id": "1755023533041", "options": ["A capacidade de criar um podcast (Resumo em Áudio).", "A capacidade de gerar um Mapa Mental interativo.", "É um substituto do Google Drive para armazenamento de arquivos na nuvem.", "A capacidade de gerar um vídeo explicativo e didático automaticamente a partir das fontes."], "question": "O vídeo \"IA Revolution\" menciona uma nova funcionalidade \"simplesmente incrível\" que foi adicionada recentemente ao NotebookLM. Qual é essa funcionalidade?", "correctAnswer": 3}, {"id": "1755023582583", "options": ["Exportar o áudio do NotebookLM para o YouTube Music.", "Usar o \"Guia de Estudo\" gerado no NotebookLM e colá-lo no Gemini para criar um mini-site interativo com um quiz.", "Sincronizar as fontes do NotebookLM com o Google Agenda para criar um cronograma de estudos.", "Criar um Mapa Mental e exportá-lo diretamente para o Google Maps."], "question": "De acordo com os tutoriais, qual das opções abaixo representa um fluxo de trabalho avançado que combina o NotebookLM com outra ferramenta do Google?", "correctAnswer": 1}, {"id": "1755023612027", "options": ["É necessário usar a opção \"Personalizar\" e adicionar um prompt de comando específico, instruindo a IA a gerar o áudio e o vídeo em português do Brasil (PT-BR).", "Basta fazer o upload de fontes que já estejam em português.", "É preciso mudar o idioma de toda a sua Conta Google para português.", "A tradução para o português só está disponível na versão paga."], "question": "O vídeo \"IA Revolution\" explica que, embora a geração de vídeo seja oficialmente em inglês, é possível instruir a IA a criar o conteúdo em português. Como isso é feito?", "correctAnswer": 0}, {"id": "1755023655947", "options": ["10 fontes.", "20 fontes.", "50 fontes.", "300 fontes."], "question": "Qual o limite de fontes (arquivos, links, etc.) que a versão gratuita do NotebookLM permite adicionar a um único notebook?", "correctAnswer": 2}], "maxAttempts": 1, "allowMultipleAttempts": false, "scoreReductionPerAttempt": 0}}', 2, '2025-08-12 18:35:11.079866', '2025-08-15 13:22:30.485', 0);
