INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (39, 'Faculdade CESURG Marau divulga resultados da Autoavaliação Institucional de 2025', 'A Faculdade CESURG Marau torna públicos os resultados da Autoavaliação Institucional realizada nos semestres 2025/1 e 2025/2, conduzida pela Comissão Própria de Avaliação (CPA). O processo integra o c', '<div>A Faculdade CESURG Marau torna públicos os resultados da Autoavaliação Institucional realizada nos semestres 2025/1 e 2025/2, conduzida pela Comissão Própria de Avaliação (CPA). O processo integra o ciclo contínuo de avaliação institucional e constitui importante instrumento de diagnóstico, planejamento e aprimoramento das práticas acadêmicas e administrativas.</div><div><br></div><div>A pesquisa foi aplicada aos acadêmicos da instituição e utilizou a metodologia Net Promoter Score (NPS), indicador internacional que mensura o nível de satisfação e o grau de recomendação da instituição. A metodologia baseia-se em uma pergunta objetiva, respondida em escala de 0 a 10, permitindo classificar os respondentes como promotores, neutros ou detratores e, assim, aferir a percepção da qualidade institucional.</div><div><br></div><div>No indicador institucional geral, referente à pergunta “Em uma escala de 0 a 10, o quanto você indicaria a Faculdade CESURG para outras pessoas?”, os resultados obtidos foram 64,2 no primeiro semestre de 2025 e 61 no segundo semestre. Os índices demonstram avaliação satisfatória da Instituição, evidenciando a consolidação de um padrão consistente de qualidade percebida pelos estudantes.</div><div><br></div><div>Quanto à avaliação do Corpo Docente, os resultados alcançaram 67 em 2025/1 e 62,5 em 2025/2, refletindo reconhecimento ao comprometimento, à qualificação e à dedicação dos professores no desenvolvimento dos processos de ensino e aprendizagem.</div><div><br></div><div>A análise comparativa entre os dois semestres permite à Instituição acompanhar a evolução dos indicadores, identificar pontos fortes e mapear oportunidades de melhoria, fortalecendo o planejamento estratégico e a tomada de decisão baseada em evidências.</div><div><br></div><div>A Comissão Própria de Avaliação destaca que a Autoavaliação Institucional é instrumento permanente de gestão, alinhado ao Plano de Desenvolvimento Institucional (PDI) e às diretrizes do Sistema Nacional de Avaliação da Educação Superior (SINAES), contribuindo para o fortalecimento da cultura avaliativa e para a promoção da melhoria contínua na Faculdade CESURG Marau.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/546_699efaf3a463a.jpg', 'https://www.cesurgmarau.com.br/noticia/546', NULL, 1, true, '2026-02-24 00:00:00', '2026-02-28 04:01:19.426367', '2026-02-28 04:01:19.426367');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (40, 'Faculdade CESURG celebra formatura dos cursos de Administração e Ciências Contábeis', 'A Faculdade CESURG Marau realizou, no último sábado (21), a cerimônia de formatura dos cursos de Administração e Ciências Contábeis, celebrando a conquista de novos profissionais na área e encerrando ', '<div>A Faculdade CESURG Marau realizou, no último sábado (21), a cerimônia de formatura dos cursos de Administração e Ciências Contábeis, celebrando a conquista de novos profissionais na área e encerrando a sequência de colações de grau públicas do último semestre.&nbsp;</div><div><br></div><div>Pelo curso de Administração, colaram grau: Bruna Casagrande, Eduarda Santos da Cruz, Fábrio Conte Lodi, Gabriela Amanda Siqueira, Itacir Madoglio Junior, Larissa P. Feronatto, Lauren Cristine Vizioli, Luiz Augusto Bedin, Marcos Antonio Canton, Milene Silva Klauck e Willian Engel.</div><div><br></div><div>Já pelo curso de Ciências Contábeis, receberam o diploma: Alessandra Busa, Arthur Bavaresco, Danieli da Trindade, Elisa Carolina Salla, Igor Miguel dos S. Roza, Janaina A. A. Gomes, Joana Elisa C. de Oliveira, Kerolin Siqueira, Larissa da Silva Censi, Laura Milena Alves, Letícia Goetz, Lucas Moura de Morais, Marceli Romanini de Oliveira, Maria Eduarda Z. Dal Pozzo, Raphael Chimento, Rodrigo Alves, Simone Caroline Faé, Stefani A. Kaller e Wilian Zanette.</div><div><br></div><div>Durante a cerimônia, o diretor da instituição, Leandro Paludo, destacou a importância do momento para a trajetória dos formandos e para a própria instituição. “Cada formatura representa a concretização de sonhos e o resultado de muito esforço e dedicação. Temos orgulho de entregar à sociedade profissionais qualificados, éticos e preparados para contribuir com o desenvolvimento das organizações e da comunidade”, afirmou.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/545_699dea7fa1d48.jpg', 'https://www.cesurgmarau.com.br/noticia/545', NULL, 1, true, '2026-02-23 00:00:00', '2026-02-28 04:01:20.042226', '2026-02-28 04:01:20.042226');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (41, 'Com mais de mil alunos, Faculdade CESURG celebra início do ano letivo', 'A Faculdade CESURG Marau iniciou o semestre letivo de 2026 com programação especial de volta às aulas. A recepção aos alunos contou com a animação dos DJs Marcos Vicenzi e Gabriel Pasin, que comandam ', '<div>A Faculdade CESURG Marau iniciou o semestre letivo de 2026 com programação especial de volta às aulas. A recepção aos alunos contou com a animação dos DJs Marcos Vicenzi e Gabriel Pasin, que comandam a trilha sonora da noite e garantiram um ambiente descontraído para dar as boas-vindas aos estudantes.</div><div><br></div><div>Com mais de mil alunos, a Faculdade CESURG consolida-se como uma das principais instituições de ensino superior da região. Atualmente, são oferecidos os cursos de Agronomia, Administração, Ciências Contábeis, Engenharia Civil, Engenharia Mecânica, Engenharia de Produção, além dos cursos técnicos em Desenvolvimento de Sistemas e Enfermagem.&nbsp;</div><div><br></div><div>O diretor da instituição, Leandro Paludo, destaca que o momento é de entusiasmo e renovação. “A volta às aulas representa recomeço, oportunidades e crescimento. Estamos preparados para mais um semestre de muito aprendizado e conquistas”, afirma Paludo.</div><div><br></div><div>Além da estrutura moderna e do corpo docente qualificado, a Faculdade CESURG reforça seu compromisso com a formação acadêmica de excelência e com o desenvolvimento regional, promovendo integração, inovação e experiências que vão além da sala de aula.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/544_6998b14892c17.jpg', 'https://www.cesurgmarau.com.br/noticia/544', NULL, 1, true, '2026-02-20 00:00:00', '2026-02-28 04:01:20.645532', '2026-02-28 04:01:20.645532');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (42, 'CESURG Marau abre processo seletivo para Bolsa Pesquisa e Participação no curso de Agronomia', 'A Faculdade CESURG Marau está com inscrições abertas para o Processo Seletivo de Bolsa Pesquisa e Participação voltado aos alunos do curso de Agronomia. Os interessados podem se inscrever entre os dia', '<div>A Faculdade CESURG Marau está com inscrições abertas para o Processo Seletivo de Bolsa Pesquisa e Participação voltado aos alunos do curso de Agronomia. Os interessados podem se inscrever entre os dias 12 de fevereiro e 20 de fevereiro.</div><div><br></div><div>A iniciativa oferece bolsa de 70% no valor do curso, oportunizando acesso ao ensino superior com incentivo à participação em projetos acadêmicos e atividades de pesquisa. O selecionado irá realizar atividades de pesquisa junto à Área Experimental da Faculdade,&nbsp; no turno da manhã ou da tarde.</div><div><br></div><div>O curso de Agronomia da CESURG Marau destaca-se pela infraestrutura voltada ao aprendizado técnico e científico, além da proximidade com o setor produtivo da região, proporcionando vivência real no campo e contato direto com as demandas do mercado.</div><div><br></div><div>O edital completo, com critérios de participação, requisitos e cronograma detalhado, está disponível no site oficial da instituição: cesurgmarau.com.br.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/543_698e4b9577819.jpg', 'https://www.cesurgmarau.com.br/noticia/543', NULL, 1, true, '2026-02-12 00:00:00', '2026-02-28 04:01:21.28071', '2026-02-28 04:01:21.28071');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (45, 'Faculdade CESURG Marau celebra formatura de novos engenheiros', 'A Faculdade CESURG Marau realizou, no último sábado (31), a cerimônia de formatura pública dos cursos de Engenharia Civil, Engenharia Mecânica e Engenharia de Produção. O evento aconteceu na Casa da C', '<div>A Faculdade CESURG Marau realizou, no último sábado (31), a cerimônia de formatura pública dos cursos de Engenharia Civil, Engenharia Mecânica e Engenharia de Produção. O evento aconteceu na Casa da Cultura e marcou a colação de grau de 12 formandos, que encerraram oficialmente um importante ciclo de formação acadêmica e iniciam uma nova etapa profissional.</div><div><br></div><div>A solenidade reuniu familiares, amigos, professores e coordenações dos cursos, em um momento de celebração e reconhecimento pela trajetória acadêmica dos estudantes. No curso de Engenharia da Produção formaram-se Eduardo Bordin e Emanuel Orsato Tibola. No curso de Engenharia Mecânica formaram-se Diego Bresolin, Igor Bianchi, Jardel Martins Mores e João Augusto Lorenzetti Franzosi. Já no curso de Engenharia Civil formaram-se Everton de Lima Soares, Jardel Peruzzo, João Victor Cardoso Bagatini, Júlia Marolli, Michel Soccol Goes e Wálisson Yuri Felini Calegari.</div><div><br></div><div>Durante a cerimônia, o diretor-geral da CESURG Marau, Leandro Paludo, destacou a importância da formação acadêmica aliada à responsabilidade profissional. “Hoje celebramos não apenas a conclusão de um curso, mas a formação de profissionais preparados para transformar a realidade onde atuarem. A engenharia tem um papel fundamental no desenvolvimento da sociedade, e temos certeza de que esses novos engenheiros levarão consigo os valores, a ética e o conhecimento construídos ao longo da graduação”, afirmou.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/540_69813fabd3831.jpg', 'https://www.cesurgmarau.com.br/noticia/540', NULL, 1, true, '2026-02-02 00:00:00', '2026-02-28 04:01:23.113904', '2026-02-28 04:01:23.113904');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (47, 'Colégio Integrado CESURG celebra formatura da segunda turma do Técnico em Enfermagem', 'No último sábado (17), a Casa da Cultura de Marau foi palco de um momento marcante para a educação e a saúde de Marau: a formatura pública da segunda turma do curso Técnico em Enfermagem do Colégio In', '<div>No último sábado (17), a Casa da Cultura de Marau foi palco de um momento marcante para a educação e a saúde de Marau: a formatura pública da segunda turma do curso Técnico em Enfermagem do Colégio Integrado CESURG. Na ocasião, 14 alunos colaram grau, celebrando a conclusão de mais uma etapa na formação profissional.</div><div><br></div><div>Além dos formandos que participaram da solenidade pública, outros seis alunos da mesma turma irão realizar a formatura de gabinete, totalizando 20 concluintes nesta edição do curso. O destaque vai além da colação de grau, ao concluírem o curso, 12 alunos da turma já foram contratados para atuar no Hospital Cristo Redentor, reforçando a qualidade do ensino oferecido pela instituição e a alta empregabilidade dos profissionais formados.</div><div><br></div><div>Para o diretor geral da Faculdade CESURG Marau, Leandro Paludo, a formatura representa muito maisque o fim de uma etapa. "Esse é um momento de grande orgulho para a nossa instituição. Ver nossos alunos concluindo a formação e, principalmente, já inseridos no mercado de trabalho, demonstra que estamos cumprindo nosso papel de formar profissionais qualificados, humanos e preparados para atender às demandas da área da saúde”, destacou.</div><div><br></div><div>O curso Técnico em Enfermagem está com últimas vagas abertas para o ano de 2026. Aproveite a oportunidade de ingressar em uma formação reconhecida, com excelente inserção no mercado de trabalho e faça a diferença na vida das pessoas. Saiba mais em cesurgmarau.com.br</div>', 'https://www.cesurgmarau.com.br/upload/noticia/538_697a364263ac6.jpg', 'https://www.cesurgmarau.com.br/noticia/538', NULL, 1, true, '2026-01-19 00:00:00', '2026-02-28 04:01:24.353144', '2026-02-28 04:01:24.353144');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (43, 'Agora também com Ensino Fundamental, Colégio Integrado CESURG inicia ano letivo de 2026', 'O Colégio Integrado CESURG deu início, nesta segunda-feira (09), ao ano letivo de 2026 para os alunos do Ensino Fundamental e do Ensino Médio, marcando um momento histórico para a instituição. Ao todo', '<div><br></div><div>O Colégio Integrado CESURG deu início, nesta segunda-feira (09), ao ano letivo de 2026 para os alunos do Ensino Fundamental e do Ensino Médio, marcando um momento histórico para a instituição. Ao todo, cerca de 120 estudantes ingressam às salas de aula, celebrando não apenas o começo de um novo período escolar, mas também uma importante expansão no projeto educacional do colégio.</div><div><br></div><div>Pela primeira vez, o Colégio Integrado CESURG passa a ofertar o Ensino Fundamental, atendendo crianças dos 6º e 7º anos, com um novo desafio pedagógico para a instituição. Já o Ensino Médio segue com turmas do 1º, 2º e 3º anos, consolidando-se como uma etapa já reconhecida pela comunidade escolar.</div><div><br></div><div>O primeiro dia de aulas foi marcado por atividades especiais. No turno da manhã, os alunos do Ensino Médio participaram de um momento de integração com música com DJ, pizza e dinâmicas de convivência, promovendo acolhimento e interação entre os colegas, com destaque para as duas novas turmas do 1º ano, que iniciam sua trajetória no colégio.</div><div><br></div><div>Já no turno da noite, o foco foi o Ensino Fundamental. Com a presença de pais e responsáveis, aconteceu a aula inaugural, um momento pensado para apresentar a proposta pedagógica, aproximar as famílias da escola e reforçar a importância dessa nova etapa para a instituição.</div><div><br></div><div>O diretor geral do Colégio Integrado CESURG, Leandro Paludo, destacou a relevância do crescimento no número de alunos e, especialmente, a ampliação da oferta educacional. “Este é um avanço muito significativo para o Colégio Integrado CESURG. O aumento no número de estudantes demonstra a confiança das famílias em nosso trabalho. Assumir o desafio de ofertar o Ensino Fundamental é um passo importante, que exige ainda mais responsabilidade, planejamento e compromisso com uma formação de qualidade desde os primeiros anos escolares”, afirmou.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/542_698e4b19d4896.jpg', 'https://www.cesurgmarau.com.br/noticia/542', NULL, 1, true, '2026-02-09 00:00:00', '2026-02-28 04:01:21.895216', '2026-02-28 04:01:21.895216');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (44, 'Faculdade CESURG Marau celebra formaturas de gabinete e colação da 7ª turma de Agronomia', 'A Faculdade CESURG Marau viveu dias de celebração e conquistas acadêmicas no último fim de semana, marcados por duas importantes cerimônias de formatura que simbolizam o encerramento de ciclos e o iní', '<div>A Faculdade CESURG Marau viveu dias de celebração e conquistas acadêmicas no último fim de semana, marcados por duas importantes cerimônias de formatura que simbolizam o encerramento de ciclos e o início de novas trajetórias profissionais para seus alunos.</div><div><br></div><div>Na sexta-feira (06), a instituição realizou a formatura de gabinete de três cursos de graduação. Pelo curso de Administração, colaram grau os acadêmicos Jewsberlie Constant, Luísa de Lima Taborda, Luiza Terres de Oliveira e Samara da Rosa. Já pelo curso de Agronomia, concluíram a graduação Daniel Zauza, Eléo Ruggini, Gustavo Triches Pasa, Jardel Rabaiolli Brustolin, Marciano Battistella e Thainan Bernardo Antunes. O curso de Engenharia de Produção teve como formando Igor Maurina Gonçalves.</div><div><br></div><div>As comemorações seguiram no sábado (07), quando a Casa de Cultura de Marau foi palco da colação de grau da 7ª turma do curso de Agronomia da CESURG Marau. Ao todo, 19 novos agrônomos receberam o diploma, oficializando a conclusão de uma etapa fundamental de suas vidas acadêmicas e profissionais.</div><div><br></div><div>Foram diplomados: Angélica Pertuzzatti Franzosi, Camile Schemitc, Gabriel Sgarbi Brugnera, Giovana Benvegnú Pandolfi, Guilherme Benedetti Oro, Gustavo Comin, Henri Cassaneli Matiasso, Iohan Dagnoluzzo, Jean Eduardo Luza, Jean Victor Filippi, João Vitor Antunes, Joel Paulo Orso, Marcio R. Linhares da Cunha, Marcos Rafael Rossetto, Pedro Henrique F. de Mello, Rivaldo Damaren, Tiago Luza, Vagner Zanon Lunardi e Vinicius Lorenzetti Sordi.</div><div><br></div><div>A Faculdade CESURG Marau parabeniza todos os formandos pelas conquistas alcançadas e reforça seu compromisso com a formação de profissionais qualificados, preparados para contribuir com o desenvolvimento regional.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/541_698cd48b85a8a.jpg', 'https://www.cesurgmarau.com.br/noticia/541', NULL, 1, true, '2026-02-16 00:00:00', '2026-02-28 04:01:22.506581', '2026-02-28 04:01:22.506581');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (46, 'V Dia de Campo de Verão 2026 da Faculdade CESURG Marau acontece dia 26 de fevereiro', 'Um dos principais eventos acadêmicos e técnicos voltados ao agronegócio da região, o V Dia de Campo de Verão da Faculdade CESURG Marau acontece no dia 26 de fevereiro. A programação ocorre das 17h às ', '<div>Um dos principais eventos acadêmicos e técnicos voltados ao agronegócio da região, o V Dia de Campo de Verão da Faculdade CESURG Marau acontece no dia 26 de fevereiro. A programação ocorre das 17h às 21h, na Área Experimental da Faculdade, e será aberta ao público, reunindo alunos, empresários do agronegócio, produtores rurais e demais interessados no setor.</div><div><br></div><div>Nesta edição, o evento contará com a participação de mais de 60 empresas, consolidando-se como um importante espaço de troca de experiências, atualização técnica e aproximação entre a academia e o mercado. Durante o Dia de Campo, os visitantes poderão conhecer de perto cultivares de soja, híbridos de milho, além de soluções apresentadas por instituições financeiras, máquinas e equipamentos agrícolas, e práticas de manejo fitossanitário, nutricional e biológico.</div><div><br></div><div>O V Dia de Campo de Verão tem como objetivo principal difundir conhecimento, apresentar novas tecnologias e fortalecer o desenvolvimento sustentável do agronegócio regional, aproximando produtores, estudantes e profissionais das inovações que impulsionam a produtividade no campo. Para o coordenador do curso de Agronomia da Faculdade CESURG Marau, Diogo da Silva Moura, o evento reforça o compromisso da instituição com a formação prática e de qualidade. “O Dia de Campo é uma oportunidade única de integrar teoria e prática, permitindo que nossos alunos, produtores e parceiros conheçam, na prática, tecnologias e manejos que fazem a diferença no dia a dia do agronegócio. É um momento de aprendizado, networking e fortalecimento do setor”, destaca o coordenador.</div><div><br></div><div>Mais informações sobre o evento através do contato (55) 99966-0261 ou pelo site cesurgmarau.com.br</div>', 'https://www.cesurgmarau.com.br/upload/noticia/539_697a36e9787d8.jpg', 'https://www.cesurgmarau.com.br/noticia/539', NULL, 1, true, '2026-01-27 00:00:00', '2026-02-28 04:01:23.727329', '2026-02-28 04:01:23.727329');
INSERT INTO public.news (id, title, description, content, image_url, source_url, category_id, creator_id, is_published, published_at, created_at, updated_at) VALUES (48, 'Colégio Integrado CESURG forma primeira turma do curso técnico em Desenvolvimento de Sistemas', 'O Colégio Integrado CESURG viveu um momento histórico na noite desta sexta-feira (19) com a formatura da primeira turma do curso técnico em Desenvolvimento de Sistemas. A colação de grau aconteceu no ', '<div>O Colégio Integrado CESURG viveu um momento histórico na noite desta sexta-feira (19) com a formatura da primeira turma do curso técnico em Desenvolvimento de Sistemas. A colação de grau aconteceu no auditório da faculdade e reuniu formandos, professores, familiares e amigos.</div><div><br></div><div>Ao todo, 20 alunos concluíram a formação, tornando-se os primeiros técnicos em Desenvolvimento de Sistemas formados pela instituição. A formação proporcionou conhecimentos em programação, lógica computacional, desenvolvimento de sistemas e soluções tecnológicas, preparando os alunos para atuar em diferentes segmentos do setor de tecnologia da informação.</div><div><br></div><div>Para o diretor geral do Colégio Integrado CESURG, Leandro Paludo, a formatura da turma pioneira representa um passo importante para a instituição. “Estamos muito orgulhosos de formar a nossa primeira turma. Esses alunos são pioneiros e saem preparados para enfrentar os desafios do mercado, aplicando o conhecimento adquirido para inovar, crescer profissionalmente e contribuir com a sociedade”, destacou.</div><div><br></div><div>Com a conclusão da primeira turma, o Colégio Integrado CESURG reforça seu compromisso com a educação de qualidade e com a formação de profissionais capacitados, reafirmando seu papel no desenvolvimento educacional e tecnológico da comunidade.</div>', 'https://www.cesurgmarau.com.br/upload/noticia/537_694e9706e333d.jpg', 'https://www.cesurgmarau.com.br/noticia/537', NULL, 1, true, '2025-12-19 00:00:00', '2026-02-28 04:01:24.961221', '2026-02-28 04:01:24.961221');
